class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      web3: '',
      myAccount: null,
      transactionInstance: null,

      ttype: null,
      name: null,
      timestamp: null,
      ipfs_hash: null,
      registrant: null,
      responsible_manager: null,
      file_type: null,
      file_description: null
    };
  }

  componentWillMount() {
    this.loadBlockchainData()
  }

  async loadBlockchainData() {
    const web3 = new Web3(new Web3.providers.HttpProvider('http://localhost:8545'));
    console.log(web3)
    const accounts = await web3.eth.getAccounts();
    console.log(accounts)
    this.setState({ myAccount: accounts[0], web3: web3});
    console.log(this.state.web3);
    this.instantiateContract();
  }

  instantiateContract() {
    const contract = require("truffle-contract");
    const transaction = contract(TransactionContract);
    console.log(this.state.web3);
    transaction.setProvider(this.state.web3.currentProvider);

    this.state.web3.eth.getAccounts((error, accounts) => {
      if (!error){
        transaction.deployed().then(instance => {
          this.setState({transactionInstance: instance, myAccount: accounts[0]});
          this.viewTransaction();
        })
      }
    })
  }

  sendTransaction() {
    this.state.transactionInstance.sendTransaction({
      from: this.state.myAvvount,
      gas: 900000
    })
  }

  //ttype에다가 일단 transaction 정보 다 넣기
  viewTransaction() {
    this.state.transactionInstance.viewTransaction.then(result => {
      this.setState ({ttype: result})
    })
  }

