        <br></br>
        <input type="text" placeholder="File Type"></input>
        <br></br>
        <input type="text" placeholder="File Description"></input>
        <br></br>

        <p>Your account: {this.state.myAccount}</p>
        <br></br>

        <button onClick={() => this.sendTransaction()}>
          트랜잭션 추가
        </button>
        <br></br>

        <p>{this.state.ttype}</p>
      </div>
