import React, { useState } from "react";
import {FileUpload} from './components/FileUpload';
import SiteName from './components/SiteName';
import TransactionAdd from './components/TransactionAdd';
import TransactionShow from './components/TransactionShow';
import TransactionInput from './components/TransactionInput';
import "./App.css";
import getWeb3 from 'web3';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      web3: null
    };
  }

  //Web3를 불러오고 state에 저장한다. this.state.web3를 통해 web3.js를 사용한다.
  componentWillMount() {
    getWeb3
      .then(results => {
        this.setState({
          web3: results.web3
        });

        this.instantiateContract();
      })
      .catch(() => {
        console.log("Error finding web3.");
      });
  }

  //truffle-contract 모듈을 불러온다. 이 모듈은 Truffle에서 작성한 컨트랙트를 Javascript로 불러오고 사용하도록 한다.
  instantiateContract() {
    const contract = require("truffle-contract");
  }

  render() {
    return <div className="App">Fruit shop</div>;
  }
}

const Main = () => {
  const [fileUrl, setFileUrl] = useState("");

  return (
    <div>

      <SiteName />
      <TransactionInput></TransactionInput>

      <FileUpload setUrl={setFileUrl} />
      FileUrl :{" "}
      <a href={fileUrl} target="_blank" rel="noopener noreferrer">
        {fileUrl}
      </a>
      <br></br>
      <TransactionAdd>트랜잭션 추가</TransactionAdd>
      <TransactionShow>트랜잭션 보기</TransactionShow>
    </div>
  );
};

export default Main;
