import {useState} from "react";
import { Card, CardBody, InputGroup, Form, FormInput, Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from "shards-react";

import "react-quill/dist/quill.snow.css";

function Editor() {
  const [open, setOpen] = useState();
  const toggle = () => {
    setOpen((o) => !o);
  };

  return (
    <Card small className="mb-4">
      <CardBody>
        <Dropdown addonType="append" open={open} toggle={toggle}>
          <DropdownToggle caret>Dropdown</DropdownToggle>
          <DropdownMenu right>
            <DropdownItem>Action</DropdownItem>
            <DropdownItem>Another action</DropdownItem>
            <DropdownItem>Something else here</DropdownItem>
          </DropdownMenu>
        </Dropdown>
        <Form className="add-new-post">
          <FormInput size="lg" className="mb-3" placeholder="카테고리" />
          <FormInput size="lg" className="mb-3" placeholder="파일명" />
          <FormInput size="lg" className="mb-3" placeholder="파일 설명" />
          <FormInput size="lg" className="mb-3" placeholder="업로더 이름" />
          <FormInput size="lg" className="mb-3" placeholder="업로드 책임자" />
        </Form>
      </CardBody>
    </Card>
  )
};

export default Editor;
