import React, { useEffect, useState, useRef } from "react";
import { Container, Row, Col, Card, CardHeader, CardBody } from "shards-react";
import PageTitle from "../common/PageTitle";
import Pagination from "../pagination.js";
import moment from "moment";

import {
  ListGroup,
  ListGroupItem,
  Form,
  FormInput,
  FormGroup,
  FormCheckbox,
  FormSelect,
  Button
} from "shards-react";


function Admin({transactionInstance}){
  
  const [limit, setLimit] = useState(10);
  const [page, setPage] = useState(1);
  const [block_list, setblock_list] = useState([]);
  const [loading, setLoading] = useState(false);
  const [first, setFirst] = useState(false);

  const [detail, setDetail] = useState(false);
  const [detailcnt, setDetailcnt] = useState(-1);

  const [trans, setTrans] = useState([]);

  const detail_click = (cnt) => {
    setDetail(true);
    setDetailcnt(cnt);
  }

  function repeatboardchild(child, start){
    if(loading){

      let arr = [];

      if(start == 1) start = 0;
      else{
        start--;
        start *= 10;
      }
  
      let end = start + 10;
  
      if (end > child.length){
        end = child.length;
      }
  
      console.log('child = ', child);
      
      console.log('child.length = %d', child.length);
      for(let i = start; i < end; i++){
        console.log('i = %d, start = %d, end = %d', i, start, end);
        console.log(sessionStorage.getItem('search'));
        // if(sessionStorage.getItem('search') === null){
          arr.push(
            <tr key={i} onClick={() => detail_click(i)} style={{cursor: "pointer"}}>
              <th scope="row" >{i+1}</th>
              <td >{child[i].name}</td>
              <td>{child[i].responsible}</td>
              <td>{child[i].filetype}</td>
              <td>{child[i].filedes}</td>
              <td>{child[i].time}</td>
            </tr>
            )
        // }
        // else if(sessionStorage.getItem('search') === child[i].name || sessionStorage.getItem('search') === child[i].responsible || sessionStorage.getItem('search') === child[i].filetype || sessionStorage.getItem('search') === child[i].filedes){
        //   arr.push(
        //     <tr key={i} onClick={() => detail_click(i)} style={{cursor: "pointer"}}>
        //       <th scope="row" >{i+1}</th>
        //       <td >{child[i].name}</td>
        //       <td>{child[i].responsible}</td>
        //       <td>{child[i].filetype}</td>
        //       <td>{child[i].filedes}</td>
        //       <td>{child[i].time}</td>
        //     </tr>
        //     )
        // }
      }
  
      return arr;
    }

  }
 
  ////////////////////////////test////////////////////////////////
  function test(child, start){
    if(loading){

      let arr = [];

      if(start == 1) start = 0;
      else{
        start--;
        start *= 10;
      }
  
      let end = start + 10;
  
      if (end > child.length){
        end = child.length;
      }
  
      console.log('child = ', child);
      
      console.log('child.length = %d', child.length);
      for(let i = start; i < end; i++){
        console.log('i = %d, start = %d, end = %d', i, start, end);
        console.log(sessionStorage.getItem('search'));
        // if(sessionStorage.getItem('search') === null){
          arr.push(
            <div>
                <a><b>Time:</b> {child[i].time}</a> <br/>
                <a><b>Address:</b> {child[i].address}</a><br/>
                <a><b>BlockHash:</b> {child[i].blockHash}</a><br/>
                <a><b>BlockNumber:</b> {child[i].blockNumber}</a><br/>
                <a><b>Id:</b> {child[i].id}</a><br/>
                <a><b>LogIndex:</b> {child[i].logIndex}</a><br/>
