import React from "react";
import PropTypes from "prop-types";
import classNames from "classnames";
import { Col, Button } from "shards-react";

import SidebarMainNavbar from "./SidebarMainNavbar";
import SidebarSearch from "./SidebarSearch";
import SidebarNavItems from "./SidebarNavItems";

import { Store } from "./../../flux";

import {Redirect} from "react-router-dom"
import Axios from 'axios';

class MainSidebar extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      menuVisible: false,
      sidebarNavItems: Store.getSidebarItems(),
      addTransaction: false
    };

    this.onChange = this.onChange.bind(this);
  }

  componentWillMount() {
    Store.addChangeListener(this.onChange);
  }

  componentWillUnmount() {
    Store.removeChangeListener(this.onChange);
  }

  onChange() {
    this.setState({
      ...this.state,
      menuVisible: Store.getMenuState(),
      sidebarNavItems: Store.getSidebarItems()
    });
  }

  handleClick () {
    axios.post("/addcctv")
      .then(() => this.setState({ condition: true }));
  }

  render() {
    const { addTransaction } = this.state;
    if (addTransaction) {
      return <Redirect to='/addcctv'/>;
    }

    const classes = classNames(
      "main-sidebar",
      "px-0",
      "col-12",
      this.state.menuVisible && "open"
    );

    return (
      <Col
        tag="aside"
        className={classes}
        lg={{ size: 2 }}
        md={{ size: 3 }}
      > 
        <SidebarMainNavbar hideLogoText={this.props.hideLogoText} />

        <Button pill theme="secondary" outline size="sm" className="mb-2" onClick={() => this.handleClick()}>
            <i className="font-size:30px material-icons mr-1">add</i> 트랜잭션 추가
        </Button>

        <SidebarSearch />
        <SidebarNavItems />
      </Col>
    );
  }
}

MainSidebar.propTypes = {
  /**
   * Whether to hide the logo text, or not.
   */
  hideLogoText: PropTypes.bool
};

MainSidebar.defaultProps = {
  hideLogoText: false
};

export default MainSidebar;
