class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      web3: null,
      myAccount: null,
      transactionInstance: null
    };
  }

  componentWillMount() {
    this.loadBlockchainData()
  }

  async loadBlockchainData() {
    const web3 = new Web3("http://127.0.0.1:8545");
    const accounts = await web3.eth.getAccounts();
    console.log(web3);
    console.log(accounts);
    this.setState({ account: accounts[0] });
    this.instantiateContract();
  }

  instantiateContract() {
    const contract = require("truffle-contract");
    const transaction = contract(TransactionContract);
    transaction.setProvider(this.state.web3.currentProvider);

    this.state.web3.eth.getAccounts((error, accounts) => {
      if (!error){
        transaction.deployed().then(instance => {
          this.setState({transactionInstance: instance, myAccount: accounts[0]});
        })
      }
    })
  }

  render() {
    return (
      <div className>
        <SiteName />
        <TransactionInput></TransactionInput>

        {/* <FileUpload setUrl={setFileUrl} />
        FileUrl :{" "}
        <a href={fileUrl} target="_blank" rel="noopener noreferrer">
          {fileUrl}
        </a> */}
        <p>Your account: {this.state.account}</p>
        <br></br>
        <TransactionAdd>트랜잭션 추가</TransactionAdd>
        <TransactionShow>트랜잭션 보기</TransactionShow>
      </div>
  )};
}

export default App;






